mapper.select:
select CASE WHEN opp.IMAGE_URL_LIST IS NULL THEN CASE WHEN opp.IMAGE_URL IS NULL THEN ( SELECT CASE WHEN IMAGE_URL_LIST IS NULL THEN IMAGE_URL ELSE IMAGE_URL_LIST END image FROM OLO_PD_PRODUCT oppt WHERE oppt.code = A .CODE AND ROWNUM = 1 ) ELSE opp.IMAGE_URL END ELSE opp.IMAGE_URL_LIST END IMAGE_URL_LIST, a."ORDER_ID", a."USER_ID", a."SKU_ID", a."CODE", a."TITLE", a."NUMBER", a."PRICE", to_char( A.CREATE_TIME,'yyyy-mm-dd hh24:mi:ss') CREATE_TIME , a."CREATION_PEOPLE_ID", to_char( A.UPDATE_TIME,'yyyy-mm-dd hh24:mi:ss') UPDATE_TIME , a."UPDATE_PEOPLE_ID", a."SPREAD1", a."SPREAD2", a."SPREAD3", a."SPREAD4", a."SPREAD5", a."ID" from "OLO_PD_ORDER_DETAILS" a LEFT JOIN OLO_PD_SKU ops on OPS.PRODUCT_CODE = a.CODE LEFT JOIN OLO_PD_PRODUCT opp on opp.goods_id = ops.goods_id --LEFT JOIN OLO_PD_PRODUCT opp on opp.CODE = a.CODE where 1 =1
AND a.USER_ID = #{pd.USER_ID}
AND a.CODE = #{pd.CODE}
AND a.SKU_ID = #{pd.SKU_ID}
AND a.ORDER_ID = #{pd.ORDER_ID}
and a.CREATE_TIME >= to_date(#{pd.lastLoginStart}, 'yyyy-MM-dd')
and a.CREATE_TIME <=to_date(#{pd.lastLoginEnd}, 'yyyy-MM-dd')
and (a.CODE like CONCAT(CONCAT('%',#{pd.key}),'%') or a.TITLE like CONCAT(CONCAT('%',#{pd.key}),'%') or a.USER_ID like CONCAT(CONCAT('%',#{pd.key}),'%'))

select CASE WHEN opp.IMAGE_URL_LIST IS NULL THEN CASE WHEN opp.IMAGE_URL IS NULL THEN ( SELECT CASE WHEN IMAGE_URL_LIST IS NULL THEN IMAGE_URL ELSE IMAGE_URL_LIST END image FROM OLO_PD_PRODUCT oppt WHERE oppt.code = A .CODE AND ROWNUM = 1 ) ELSE opp.IMAGE_URL END ELSE opp.IMAGE_URL_LIST END IMAGE_URL_LIST, a."ORDER_ID", a."USER_ID", a."SKU_ID", a."CODE", a."TITLE", a."NUMBER", a."PRICE", to_char( A.CREATE_TIME,'yyyy-mm-dd hh24:mi:ss') CREATE_TIME , a."CREATION_PEOPLE_ID", to_char( A.UPDATE_TIME,'yyyy-mm-dd hh24:mi:ss') UPDATE_TIME , a."UPDATE_PEOPLE_ID", a."SPREAD1", a."SPREAD2", a."SPREAD3", a."SPREAD4", a."SPREAD5", a."ID" from "OLO_PD_ORDER_DETAILS" a LEFT JOIN OLO_PD_SKU ops on OPS.PRODUCT_CODE = a.CODE LEFT JOIN OLO_PD_PRODUCT opp on opp.goods_id = ops.goods_id --LEFT JOIN OLO_PD_PRODUCT opp on opp.CODE = a.CODE where 1=1
AND a.ORDER_ID in
#{item.ORDER_ID}
and a.CREATE_TIME >= to_date(#{lastLoginStart}, 'yyyy-MM-dd')
and a.CREATE_TIME <=to_date(#{lastLoginEnd}, 'yyyy-MM-dd')
AND a.ORDER_ID = #{ORDER_ID}
and (a.CODE like CONCAT(CONCAT('%',#{key}),'%') or a.TITLE like CONCAT(CONCAT('%',#{key}),'%') or a.USER_ID like CONCAT(CONCAT('%',#{key}),'%'))
order by ${sort} ${order}
order by a.CREATE_TIME desc
